import React, {Component } from 'react';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    Button } from 'reactstrap';

class TestPage extends Component {
    // basically just bring the animation program here
    // get canvas through something like getById
    // gotta make a constructor in order to do state
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
          isOpen: false,
          holes: [],
          intervals: [20,20,20,20,20,20,20,20,20],
          counter: 0,
          index: 0,
          stopped: true,
          once: false,
          numPegs: 9
        };
      }

    draw() {
        if (!stopped) {
            drawHoles();
        counter++;
        if (index < intervals.length && counter == intervals[index] - 10 && !once) {
                numPegs--;
        } else if (index < intervals.length && counter == intervals[index]) {
            numPegs++;
            counter = 0;
        }

        if (index < intervals.length && counter == intervals[index] - 5) {
            holes[index].changeColor();
            index++;
            if (!once) {
                counter = 0;
            }
        } else if (index >= intervals.length && !once) {
            index = 0;
            counter = 0;
            once = true;
        } else if (index >= intervals.length) {
            stopped = true;
            numPegs++;
            drawHoles();
        }
        console.log(counter);
        }
    }

    begin() {
        stopped = false;
        this.intervalHandle = setInterval(this.tick, 1000);
    }

      toggle() {
        this.setState({
          isOpen: !this.state.isOpen
        });
      }

    render() {
        const spacingContainer = {
            height: '40px'
          };
          const buttonStyle = {
            height: '150px',
            width: '400px'
          }
        return (
            <div>
                <div class="container" style={spacingContainer}></div>
                <div class="container mx-auto text-center img-thumbnail shadow-lg p-3 mb-5 bg-white rounded">
                    <canvas id="myCanvas" width="510" height="510"></canvas>
                </div>
                <div class="container mx-auto text-center">
                    <button type="button" class="btn btn-outline-dark text-center btn-lg" style={buttonStyle}>Begin Test</button>
                </div>

                <script src="animation.js"></script>
            </div>
        )
    }
}

export default TestPage;
